mapper.select:
SELECT 

FROM completion a

WHERE a.id = #{id}

SELECT 

FROM completion a


 a.is_delete = '0'

  and a.couse_id = #{course.id}


  and a.completion_title LIKE CONCAT('%', #{completionTitle},'%')  


  and a.degree = #{degree}


  and a.create_time >= #{bTime}


  and a.create_time <= #{eTime}


  and tc.teaid = #{teaId}

mapper.update:
UPDATE completion SET 
	is_delete = 1
WHERE id = #{id}
