SELECT 

FROM examrule a


 a.is_delete = '0'

  and b.couname LIKE CONCAT('%',#{couname},'%')  


  and a.rule_name LIKE CONCAT('%',#{ruleName},'%')  


  and a.rule_type = #{ruleType}


  and a.couse_id = #{course.id}


  and tc.teaid = #{teaId}

mapper.update:
