mapper.select:
SELECT c.id, c.couname, d.dicname, c.credit, c.is_delete FROM course c, dictionaries d WHERE is_delete = 0 AND c.ctype =d.dvalue AND d.dtype = "课程类型"
and c.ctype = #{ctype}
and c.couname like concat('%',#{couname},'%')
limit #{page}, #{rows}

select count(*) from course where is_delete = 0
and ctype = #{ctype}
and couname like concat('%',#{couname},'%')

mapper.update:
UPDATE course SET 
	is_delete = 1
WHERE id = #{id}

  	update course set 	
	couname = #{couname},
	credit = #{credit},
	ctype = #{ctype}
	where id = #{id}
