mapper.select:
SELECT s.* 
FROM account a, studentinfo s 
WHERE a.`userdetailid` = s.`id` AND a.`username` = #{username}

mapper.update:
update studentinfo
set stuphone = #{stuphone}
where stuno = #{stuno}

update studentinfo
set stuimage= #{stuimage}
where stuno = #{stuno}

mapper.select:
select s.stuno,s.stuname,c.claname,c.college,c.major,c.classyear
from studentinfo s , class c
where 1=1

	and s.stuno = #{stuno}


	and s.stuname like #{stuname}

and s.classid = c.id
limit #{page}, #{rows}

select count(*)
from studentinfo s , class c
where 1=1

	and s.stuno = #{stuno}


	and s.stuname like #{stuname}

and s.classid = c.id

select s.stuno,s.stuname,c.claname,c.college,c.major,c.classyear
from studentinfo s , class c
where 1=1

	and s.stuno = #{stuno}


	and s.stuname like #{stuname}

and s.classid = c.id

select * 
from accessory 
where 1=1

	and accname like #{accname}


	and uploadtime >= #{beforeuploadtime}


	and uploadtime <= #{afteruploadtime}


	and uploadtime between #{beforeuploadtime} and #{afteruploadtime}

and ownid = #{ownid}
and acctype = 1
limit #{page}, #{rows}

select count(*) 
from accessory 
where 1=1

	and accname like #{accname}


	and uploadtime >= #{beforeuploadtime}


	and uploadtime <= #{afteruploadtime}


	and uploadtime between #{beforeuploadtime} and #{afteruploadtime}

