mapper.select:
SELECT ad.* 
FROM account a, adminsinfo ad 
WHERE a.`userdetailid` = ad.`id` AND a.`username` = #{username}

SELECT * 
FROM adminsinfo
WHERE adminno = #{adminno}

mapper.update:
	update adminsinfo
  	set adminphone = #{adminphone}, adminemail = #{adminemail}
  	where adminno = #{adminno}

mapper.select:
SELECT * 
FROM account
WHERE username = #{username}

mapper.update:
	update account
  	set password = #{password}
  	where username = #{username}

mapper.select:
select imgno, imgname, imgurl, create_time, is_pub from rollpicture where is_delete = 0
and imgname like concat('%',#{imgname},'%')
limit #{page}, #{rows}

select j.id, j.joutitle, j.joucontent, j.joutype, j.starttime, j.endtime, a.adminname from journalism j, adminsinfo a where j.pubid = a.id and j.is_delete = 0 and j.j_type = #{j_type}
and j.joutitle like concat('%',#{joutitle},'%')
and j.joucontent like concat('%',#{joucontent},'%')
limit #{page}, #{rows}

select count(*) from journalism where is_delete = 0 and j_type = #{j_type}
and joutitle like concat('%',#{joutitle},'%')
and joucontent like concat('%',#{joucontent},'%')

select j.id, j.joutitle, j.joucontent, j.joutype, j.starttime, j.endtime, a.adminname from journalism j, adminsinfo a where j.pubid = a.id and j.is_delete = 0 and j.j_type = #{j_type}
and j.joutitle like concat('%',#{joutitle},'%')
and j.joucontent like concat('%',#{joucontent},'%')

mapper.update:
update journalism
set joutitle = #{joutitle}, joucontent = #{joucontent}, joutype = #{joutype}
where id = #{id}

mapper.select:
select id, dicname, dtype, dvalue, remark, is_delete
from dictionaries
where is_delete = 0

	and dtype = #{dtype}

limit #{page}, #{rows}

select couname, ctype, credit, is_delete
from course
where is_delete = 0

	and dtype = #{dtype}


	and couname like concat('%',#{couname},'%')

limit #{page}, #{rows}

