mapper.select:
select id, dicname, dtype, dvalue, remark, is_delete
from dictionaries
where is_delete = 0

	and dtype = #{dtype}

limit #{page}, #{rows}

mapper.update:
UPDATE dictionaries SET 
	is_delete = 1
WHERE id = #{id}

  	update dictionaries set 	
	dicname = #{dicname},
	remark = #{remark},
	dtype = #{dtype},
	dvalue = #{dvalue}
	where id = #{id}
